    public ListNode reverseList(ListNode head) {
        ListNode prev = null;
        ListNode current = head;

        while (current != null) {
            // Save next node
            ListNode next = current.next;

            // Reverse the pointer
            current.next = prev;

            // Move pointers one step forward
            prev = current;
            current = next;
        }

        return prev; // New head of the reversed list
    }
}

